import React, { Component } from 'react'
import {View, Text, TouchableOpacity, StyleSheet, Image, TextInput} from 'react-native'
import {Camera} from 'expo-camera'
import { storage } from '../firebase/config'

class MiCamara extends Component {
    constructor(props){
        super(props)
        this.state={
            permisos: false,
            urlFoto:'',
            mostrarCamara: true
        }
        this.metodosDeCamara = undefined
    }

    componentDidMount(){
        Camera.requestCameraPermissionsAsync()
        .then(()=>{
            this.setState({permisos: true})
        })
        .catch(error => console.log(error))
        console.log(Camera)
    }

    tomaLaFoto(){
        console.log('say cheese')
        this.metodosDeCamara.takePictureAsync()
        .then(dataFoto => {
            console.log(dataFoto)
            this.setState({
                urlFoto: dataFoto.uri,
                mostrarCamara:false
            })
            console.log(dataFoto)
        })
        .catch(error => console.log(error))
    }
    guardarFoto(){
        fetch(this.state.urlFoto)
        .then(response => {
            return response.blob()
        })
        .then(foto => {
            const referenciaDelStorage = storage.ref(`photos/${Date.now()}.jpg`)

            referenciaDelStorage.put(foto)
            .then(()=>{
                referenciaDelStorage.getDownloadURL()
                .then( url => {
                    console.log(url)
                    this.props.cuandoSubaLaImagen(url);
                    this.setState({photo:''})
                })
            })
        })
        .catch(error => console.log(error))
    }

    descartarFoto(){
        console.log('Foto descartada')
    }


  render() {
    return (
        <>
        {
            this.state.mostrarComponenteCamara ?
            <MiCamara cuandoSubaLaImagen={(url)=> this.cuandoSubaLaImagen(url)}/>
            :

            <View>
                <Text>Enviale un mensaje a Facu</Text>
                <TextInput 
                style={styles.textarea}
                onChangeText= {(text)=> this.setState({
                    message: text
                })}
                value={this.state.message}
                />
                <TouchableOpacity
                    style={styles.btn}
                    onPress={() => {
                        this.props.route.params.newMessage(this.state.message, this.state.urlFoto)
                        this.setState({
                            message:'',
                        })
                        this.props.navigation.navigate('Home')
                    }}
                >
                    <Text>Enviar mensaje</Text>
                </TouchableOpacity>
            </View>
        }
        </>

    )
  }
}

const styles = StyleSheet.create({
    container:{
        flex:1
    },
    camara:{
      flex:7  
    },
    button:{
        flex:1,
        justifyContent:'center',
        alignItems: 'center'
    },
})

export default MiCamara
